INSERT INTO `wp_wc_admin_notes` VALUES("28", "woopay-beta-merchantrecruitment-update-04MAY23", "info", "en_US", "Increase conversions with WooPay  our fastest checkout yet", "WooPay, a new express checkout feature built into WooCommerce Payments, is now available  and youre invited to try it. \r\n<br /><br />\r\nBoost conversions by offering your customers a simple, secure way to pay with a single click.\r\n<br /><br />\r\nUpdate WooCommerce Payments to get started.", "{}", "pending", "woocommerce.com", "2025-12-05 15:03:18", NULL, "0", "plain", "", "0", "0", "info");
